sqlReaderQuery.value:
@if(contains(
        createArray('GB', 'SU'), variables('autolineModule')),
    concat(
        'select * from openquery([', pipeline().parameters.linkedServer, 
        '], ''select c.DESCU, c.COMPANY from ', variables('catalogueTableName'), 
        ' where filename like ''''%', variables('autolineFilename'), '%'''' '')'
    ),
    concat(
        'select * from openquery([', pipeline().parameters.linkedServer, 
        '], ''select c.DESCU, c.COMPANY from ', variables('catalogueTableName'), 
        ' where filename like ''''%', variables('autolineFilename'), '%'''' and c.module_ = p.module_ and c.company = p.company and p.user_ = ''''', variables('odbcUser'), ''''' and p.priority > 0'')'
    )
)

