DECLARE @TempTable SYSNAME = 'dbo._temp_@{item().DESCU}_@{pipeline().parameters.linkedServer}'; -- Staging temp table
BEGIN TRY

-- ========================================
-- Step 1: Drop the temporary table
-- ========================================
    DECLARE @DropSQL NVARCHAR(MAX) = 'DROP TABLE ' + @TempTable + ';';
    EXEC sp_executesql @DropSQL;
    print @DropSQL;
END TRY
BEGIN CATCH
    PRINT 'Error occurred: ' + ERROR_MESSAGE();
    THROW;
END CATCH

